Entanglement Generator.command:
const tableHeatA = 'Напасти - Уровень Подозрений 0-3';  // Entanglements - Heat 0-3
const tableHeatB = 'Напасти - Уровень Подозрений 4-5';  // Entanglements - Heat 4/5
const tableHeatC = 'Напасти - Уровень Подозрений 6+';   // Entanglements - Heat 6+
const compendiumTables = 'Таблицы напастей';       // Compendium name 
const version = '1.2';
main();

