Discharged CorpKiller Create.command:
(async () => {
  if (!actor || !actor.findItem("feat", "Эксперт по гранатам")) {
    return;
  }
  const grenade = actor.findItem("weapon", "Ручная граната");
  const flashbang = actor.findItem("weapon", "Оглушающая граната");
  if (grenade) {
    const quantity = actor.system.abilities.toughness.value + 5;
    await grenade.update({["system.quantity"]: quantity});
  }
  if (flashbang) {
    await flashbang.update({["system.quantity"]: 5});
  }
})();

Orphaned Gearhead Create.command:
(async () => {
  if (!actor) {
    return;
  }
  const vehicle = actor.ownedVehicles().shift();
  if (!vehicle) {
    return;
  }

  let hpFormula = null;
  const descript = vehicle.system.description.toLowerCase();
  if (descript.includes("Полуавтономный шагоход")) {
    hpFormula = "1d8+@abilities.knowledge.value";
  } else if (descript.includes("Летающий дрон")) {
    hpFormula = "1d12+@abilities.knowledge.value";
  } else if (descript.includes("Прототип гусеничного дрона")) {
    hpFormula = "1d10+@abilities.knowledge.value";
  }
  if (hpFormula) {
    const roll = new Roll(hpFormula, actor.getRollData()).evaluate({async: false});
    await vehicle.update({
      ["system.hitPoints.max"]: roll.total, 
      ["system.hitPoints.value"]: roll.total, 
    });
  }
})();

Starting Cyberdeck Slots.command:
(async () => {
  if (!actor) {
    console.error("Ни один актер не передан в макрос");
    return;
  }
  const item = actor.findItem("cyberdeck", "Стартовая кибердека");
  if (!item) {
    console.log("Стартовая кибердека не найдена");
    return;
  }
  const slots = new Roll("d3").evaluate({async: false}).total;
  await item.update({["system.slotFormula"]: slots.toString()});
})();
