Generate Hex.command:
const compendium_label = CONFIG.MAUSRITTER.tables.tables;

(async () => {
	const hexTypeList = [
		"Случайно",
		"Сельская местность",
		"Лес",
		"Река",
		"Человеческий город",
	];
	let selectList = "";

	for (const option of hexTypeList) {
		selectList += `<option value='${option}'>${option}</option>`;
	}

	const d = new Dialog({
		title: "Выбор типа гекса",
		content: `<h2> Выберите тип гекса </h2> <select style='margin-bottom:10px;'name='stat' id='stat'> ${selectList}</select> <br/>`,
		buttons: {
			roll: {
				icon: '<i class="fas fa-check"></i>',
				label: "Бросок",
				callback: (html) => hexType(html.find('[id="stat"]')[0].value),
			},
			cancel: {
				icon: '<i class="fas fa-times"></i>',
				label: "Отмена",
				callback: () => {},
			},
		},
		default: "roll",
		close: () => {},
	});
	d.render(true);
})();

async function hexType(hexType) {
	let type = hexType;
	let hexContents = "";
	let hexDetails = "";
	let buffer = "";
	const randomHex = ["Сельская местность", "Лес", "Река", "Человеческий город"];

	if (type === "Случайно") {
		type = randomHex[Math.floor(Math.random() * randomHex.length)];
	}

	buffer = await drawFromTable(`Гекс - ${type}`);
	buffer = await buffer.roll();
	hexContents = buffer.results[0].data.text;

	buffer = await drawFromTable("Гекс - Интересная деталь");
	buffer = await buffer.roll();
	hexDetails = buffer.results[0].data.text;

	const message = `
  <h2>${type}: </h2>
  <b> Ориентир: </b>${hexContents}</br>
  <b> Деталь: </b><i>${hexDetails}</i></br>`;

	const chatData = {
		content: message,
		whisper: ChatMessage.getWhisperRecipients("GM"),
	};

	ChatMessage.create(chatData);
}

async function drawFromTable(tableName) {
	const list_compendium = await game.packs.filter(
		(p) => p.documentName === "RollTable",
	);
	const inside = await list_compendium
		.filter((p) => p.metadata.label === compendium_label)[0]
		.getDocuments();
	const table = await inside.filter((p) => p.data.name === tableName)[0];

	if (!table) {
		ui.notifications.warn(`Таблица ${tableName} не найдена.`, {});
		return;
	}
	return await table;
}

NPC Generator.command:
const compendium_label = CONFIG.MAUSRITTER.tables.tables;

(async () => {
	const Appearance = await drawFromTable(
		CONFIG.MAUSRITTER.tables.npcAppearance,
	);
	const Birthsign = await drawFromTable(CONFIG.MAUSRITTER.tables.npcBirthsign);
	const Quirk = await drawFromTable(CONFIG.MAUSRITTER.tables.npcQuirk);
	const Social = await drawFromTable(CONFIG.MAUSRITTER.tables.npcSocial);
	const Wants = await drawFromTable(CONFIG.MAUSRITTER.tables.npcWants);
	const Relationship = await drawFromTable(
		CONFIG.MAUSRITTER.tables.npcRelationship,
	);
	const Birthname = await drawFromTable(CONFIG.MAUSRITTER.tables.firstName);
	const Matriname = await drawFromTable(CONFIG.MAUSRITTER.tables.lastName);

	const dexterity = attrRoll();
	const strength = attrRoll();
	const will = attrRoll();
	const health = healthRoll();

	let msg = "";
	msg += `<p><b>Положение в обществе и плата за услуги:</b> ${Social}</p>`;
	msg += `<p><b>Внешность:</b> ${Appearance}</p>`;
	msg += `<p><b>Особенность:</b> ${Quirk}</p>`;
	msg += `<p><b>Желания:</b> ${Wants}</p>`;
	msg += `<p><b>Отношения:</b> ${Relationship}</p>`;

	//let npchp = randomHP(4,10);
	const instantNPC = await Actor.create({
		name: `${Birthname} ${Matriname}`,
		type: "hireling",
		data: {
			description: {
				disposition: Birthsign,
			},
			notes: msg,
			stats: {
				dexterity: {
					max: dexterity,
					value: dexterity,
				},
				strength: {
					max: strength,
					value: strength,
				},
				will: {
					max: will,
					value: will,
				},
			},
			health: {
				max: health,
				value: health,
			},
		},
	});

	await instantNPC.sheet.render(true);
})();

async function drawFromTable(tableName) {
	const list_compendium = await game.packs.filter(
		(p) => p.documentName === "RollTable",
	);
	const inside = await list_compendium
		.filter((p) => p.metadata.label === compendium_label)[0]
		.getDocuments();
	const table = await inside.filter((p) => p.data.name === tableName)[0];

	if (!table) {
		ui.notifications.warn(`Таблица ${tableName} не найдена.`, {});
		return;
	}

	const buffer = await table.roll();

	return buffer.results[0].data.text;
}

function treasureCoins(min, max) {
	return Math.floor(Math.random() * (max - min)) + min;
}

function attrRoll() {
	return new Roll("2d6").roll({ async: false }).total;
}

function healthRoll() {
	return new Roll("1d6").roll({ async: false }).total;
}

function randomHP(min, max) {
	return Math.floor(Math.random() * (max - min)) + min;
}

