const compendium_label = CONFIG.MAUSRITTER.tables.tables;

(async () => {
	const seasonList = ["Лето", "Осень", "Зима", "Весна"];
	let selectList = "";

	for (const option of seasonList) {
		selectList += `<option value='${option}'>${option}</option>`;
	}

	const d = new Dialog({
		title: "Выбор времени года",
		content: `<h2> Время года </h2> <select style='margin-bottom:10px;'name='stat' id='stat'> ${selectList}</select><br/>`,
		buttons: {
			roll: {
				icon: '<i class="fas fa-check"></i>',
				label: "Бросок",
				callback: (html) =>
					drawFromTable(`Погода - ${html.find('[id="stat"]')[0].value}`),
			},
			cancel: {
				icon: '<i class="fas fa-times"></i>',
				label: "Отмена",
				callback: () => {},
			},
		},
		default: "roll",
		close: () => {},
	});
	d.render(true);
})();

async function drawFromTable(tableName) {
	const list_compendium = await game.packs.filter(
		(p) => p.documentName === "RollTable",
	);
	const inside = await list_compendium
		.filter((p) => p.metadata.label === compendium_label)[0]
		.getDocuments();
	const table = await inside.filter((p) => p.data.name === tableName)[0];

	if (!table) {
		ui.notifications.warn(`Таблица ${tableName} не найдена.`, {});
		return;
	}

	let weather = await table.roll();
	weather = weather.results[0].data.text;
	const message = `<h2>Погода: </h2><b style='font-size:120%;'>${weather}</br>`;

	const chatData = {
		content: message,
		whisper: ChatMessage.getWhisperRecipients("GM"),
	};

	ChatMessage.create(chatData);
}
